) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- 转存表中的数据 `hs`
--

INSERT INTO `hs` (`id`, `title`, `new`, `picture`, `time`, `author`, `djcs`, `shzt`) VALUES
(1, '红石元件', '红石元件是在红石电路里具有一定使用目的的方块，大致分为三个大类。\r\n<br>\r\n&nbsp; &nbsp;\r\n电源为整个电路或部分电路提供信号来源，例如红石火把、按钮、拉杆、红石块、压力板等。\r\n<br>\r\n&nbsp; &nbsp;\r\n传输元件将信号从电路的一部分传递到另一部分，例如红石粉、红石中继器、红石比较器等。\r\n<br>\r\n&nbsp; &nbsp;\r\n机械元件接受信号并作出反应（例如移动、发光等），例如活塞、红石灯、发射器等。\r\n<br>\r\n&nbsp; &nbsp;\r\n由于红石火把、红石粉、红石中继器、红石比较器这四个方块既可以被激活，又可以激活其他元件，因此也可单独划分为一类，称为红石元器件。', '1.png', '2023_5_1', 'yancey', 7, 0),
(2, '位置', '一个方块占据一个正方体的空间，正方体有6个面。也就是说与一个方块的六个面直接接触的方块有6个，称之为“与该方块毗邻的方块”，简称毗邻。\r\n<br>\r\n&nbsp; &nbsp;\r\n一个方块的毗邻和毗邻的毗邻（即与这个方块的曼哈顿距离为两格以内的方块），通称为这个方块的二阶毗邻。一个方块的毗邻的毗邻共有19个方块（包括它本身），二阶毗邻共有25个方块（包括它本身）。\r\n<br>\r\n&nbsp; &nbsp;\r\n四周一般指的是与该方块东西南北四个面相接触的4个方块。\r\n<br>\r\n&nbsp; &nbsp;\r\n红石线、红石火把等方块需要附着在其他方块上，一旦所附着的方块被破坏，它本身也会掉落。它们所附着的方块简称为它们的附着。\r\n<br>\r\n&nbsp; &nbsp;\r\n一些电源和传输元件有它的指向。中继器、比较器的指向就是输出端所朝向的方块，红石火把的指向是正上方的那一个方块，红石粉的指向通常与它的纹理的朝向相同，但也有特殊情况。', '2.png', '2023_5_1', 'yancey', 7, 0),
(3, '电路与机械', '两个术语通常都用于指包含红石元件的结构，但两者一般还是有一定区别的：\r\n<br>\r\n&nbsp; &nbsp;\r\n电路（circuit）为处理信号的结构（产生，传输，修改，组合等）。\r\n<br>\r\n&nbsp; &nbsp;\r\n机械（mechanism）会对环境产生影响（移动方块，开门，改变光照强度，播放声音等）。\r\n<br>\r\n&nbsp; &nbsp;\r\n所有机械一定包含电路，因此也可称为机械电路。但电路本身是不会对环境产生影响的（除了红石火把等产生的光，或活塞作为电路组成成分之一时造成的推拉方块的副效果）。复杂的电路还可以细分为数字电路和模拟电路。参见下文基本种类章节', '3.png', '2023_5_1', 'yancey', 12, 0),
(4, '信号与脉冲', '红石电路能够产生信号——“有/无信号”时称为“1/0”、“真/假”或“高电平/低电平”。\r\n<br>\r\n&nbsp; &nbsp;\r\n上边沿/上升沿指红石信号由无到有的变化瞬间；下边沿/下降沿指红石信号由有到无的变化瞬间。\r\n<br>\r\n&nbsp; &nbsp;\r\n相（Phase）代表相位，指逻辑代数中的“相同”与“相反”（例如“1”与“0”互为反相）。\r\n<br>\r\n&nbsp; &nbsp;\r\n当信号出现一个较为短暂的“0-1-0”过程，该过程通常被称为脉冲（或正脉冲。“1-0-1”的过程被称为负脉冲）。脉冲持续的时间长度被称为脉冲的长度。', '4.png', '2023_5_1', 'yancey', 9, 0),
(5, '信号强度', '信号强度通常为0到15的整数。红石线能向相邻的红石线传导信号，但每传导1格，信号强度就降低1。因此，连续的红石线最远能将信号传到15格远。为了传输更远，可以使用红石比较器来保持信号强度或使用红石中继器将信号强度加至15。\r\n<br>\r\n&nbsp; &nbsp;\r\n信号强度会因为红石线之间的直接传导而衰减，也可以通过测重压力板、阳光探测器或红石比较器直接控制输出不同的信号强度。\r\n<br>\r\n&nbsp; &nbsp;\r\n红石比较器实际上可以接受和输出小于0或超过15强度的信号。如命令方块的成功次数超过15或容器内的物品超过堆叠限制，红石比较器会把它们作为强度小于0或超过15的信号进行比较或作差后输出。', '5.png', '2023_5_1', 'yancey', 9, 0),
(6, '充能', '部分方块能够被充能及解除充能。当红石信号作用于一个方块时，如果这个方块毗邻的所有机械元件都可以被激活，那么这个方块就被称为红石导体，这个行为被称为充能。被充能的方块叫做充能方块。大多数红石导体都是不透明的方块。\r\n<br>\r\n<br>\r\n强充能与弱充能\r\n<br>\r\n&nbsp; &nbsp;\r\n充能又分为强充能和弱充能：\r\n<br>\r\n&nbsp; &nbsp;\r\n强充能的方块可以激活毗邻的机械元件和红石元器件。红石导体可以被电源及中继器、比较器强充能。被强充能的方块所表现性质和红石块大体相同，但是红石块不是红石导体，且可以改变红石线的形状和指向、向红石比较器边侧输入信号[仅Java版]，而强充能方块不能。\r\n当红石导体仅被红石线充能，我们称这个方块被弱充能。弱充能与强充能的区别在于，弱充能方块无法激活毗邻的红石线。\r\n充能方块无法再去充能其他方块，只有电源和传输元件才可以去充能一个方块。\r\n<br>\r\n<br>\r\n充能等级\r\n<br>\r\n&nbsp; &nbsp;\r\n使用多少信号强度的信号去充能一个方块，我们就说这个方块有多少充能等级。被强充能的方块有多少充能等级，就可以激活毗邻的红石粉至多少信号强度。充能等级的大小与充能的强与弱完全无关。', '6.png', '2023_5_1', 'yancey', 6, 0),
(7, '激活', '机械元件和红石元器件可被电源、充能方块和传输元件以特定的方式激活，引发机械元件和红石元器件的反应（如活塞推动方块，开门，红石火把熄灭等）。\r\n<br>\r\n&nbsp; &nbsp;\r\n有些机械元件只会在刚被激活的瞬间有所反应，直到取消激活并再激活之前都不会再有所反应（如命令方块执行命令，投掷器与发射器发射物品，音符盒播放一个音符）。其他机械元件会在激活时始终保持状态，直到取消激活（红石灯保持点亮，门保持开启，漏斗保持锁定状态，活塞保持伸出等）。\r\n<br>\r\n&nbsp; &nbsp;\r\n所有机械元件都可以被下列方块激活（例外：活塞不会被其活塞头朝向的方块常规激活）：\r\n<br>\r\n&nbsp; &nbsp;\r\n毗邻的，处于开启状态的电源\r\n例外：红石火把不会激活其附着的机械元件，侦测器不会激活除其指向以外的方块。\r\n毗邻的充能方块（强充能与弱充能均可）\r\n指向该机械元件的、输出信号的红石比较器或红石中继器\r\n指向该机械元件的、激活的红石粉，包括附着其上的红石粉。（毗邻的、但未指向机械元件的红石粉不会激活机械元件）\r\n<br>\r\n&nbsp; &nbsp;\r\n部分机械元件可以用其他方式激活：\r\n<br>\r\n&nbsp; &nbsp;\r\n在Java版中，发射器、投掷器与活塞可以被以下方式激活：即如果上述4种方式作用于该机械元件正上方的那个方块（无论是何种方块，即使是空气也无妨），该机械元件也会被激活。正如门的下半部分那样。这种情况也可以表述为：该元件可以被斜上方或上方2格的方块激活。下图即为这类激活方式的例子。 这种方式被称为半连接。\r\n门占2格空间，激活任意一半门，另一半也会被激活。', '7.png', '2023_5_1', 'yancey', 6, 0),
(8, '充能与激活', '对于属于红石导体的机械元件（包括命令方块、投掷器、发射器、音符盒与红石灯等等），既可以被充能也可以被激活。因此区分它们是被激活还是被充能相当重要：\r\n<br>\r\n&nbsp; &nbsp;\r\n如果它能够激活毗邻的红石元器件和机械元件，那么它就被充能了。\r\n<br>\r\n&nbsp; &nbsp;\r\n如果它本身作出了一定的反应，那么它就被激活了。\r\n机械元件只要被充能就一定也会被激活，但被激活并不会一定会被充能（如被毗邻的充能方块激活）。\r\n<br>\r\n&nbsp; &nbsp;\r\n不是红石导体的机械元件（门、栅栏门、活塞、漏斗、铁轨、活板门等等）可被激活并作出反应，但因为不具备红石导体的性质而无法被充能。', '8.png', '2023_5_1', 'yancey', 6, 0),
(9, '方块更新（狭义）', '当一个方块发生状态的改变时，该改变会引起周围方块的方块更新。\r\n<br>\r\n&nbsp; &nbsp;\r\n在Java版中，红石电路的运作依赖于方块更新机制。单次方块更新会使得周围的其他红石元件接收到“附近发生变化”的提示，以检测自身是否应该发生变化。如果发生了变化，将又会对周围发出方块更新。更新引起变化、变化创造更新，这将会是个连锁反应，电路不一定能达到完全稳定的状态。\r\n<br>\r\n&nbsp; &nbsp;\r\n在Java版中，充能并非游戏内部的机制，因此，不管是否被充能或解除充能都不能产生方块更新。元件发生变化时，会更新周围足够范围内的方块以使电路正常工作（例如，压力板更新其毗邻及其附着的毗邻）。\r\n<br>\r\n&nbsp; &nbsp;\r\n除了方块更新之外，红石比较器还可以通过其后方两格内的容器（包括上有运输矿车的探测铁轨）和某些其他方块的变化（例如物品栏里的物品发生变动）进行更新；侦测器还可以检测某些方块变化而进行更新。\r\n<br>\r\n&nbsp; &nbsp;\r\n以下为Java版中的通常情况下的方块更新范围：', '9.png', '2023_5_1', 'yancey', 17, 0),
(10, '红石系统', '红石系统是游戏用来管理红石电路的一个机制。在基岩版中，红石电路的运作主要依赖于红石系统，同时也会受到方块更新的影响。\r\n<br>\r\n&nbsp; &nbsp;\r\n红石系统储存了所有的红石元件及其附近的实体方块的信息及连接方式。每两游戏刻，红石系统就进行一次信号更新，计算和设置各个红石元件的红石信号。', '10.png', '2023_5_1', 'yancey', 8, 0);

-- --------------------------------------------------------

--
-- 表的结构 `ml`
--

CREATE TABLE `ml` (
  `id` int(11) NOT NULL,
  `title` varchar(100) COLLATE utf8_unicode_ci NOT NULL,
  `new` varchar(5000) COLLATE utf8_unicode_ci NOT NULL,
  `picture` varchar(100) COLLATE utf8_unicode_ci NOT NULL,
  `time` varchar(100) COLLATE utf8_unicode_ci NOT NULL,
  `author` varchar(50) COLLATE utf8_unicode_ci NOT NULL,
  `djcs` int(11) NOT NULL,
  `shzt` int(11) NOT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- 转存表中的数据 `ml`
--

INSERT INTO `ml` (`id`, `title`, `new`, `picture`, `time`, `author`, `djcs`, `shzt`) VALUES
(1, '使用方法', '在客户端中，命令通过Minecraft的聊天窗口输入，通过按下T键、Chat button.png[仅基岩版和教育版]、D-Pad right[仅基岩版]（默认）或/键唤出聊天窗口。使用/键会同时输入命令必需的前缀斜杠（/），因此这是个实用的快捷键。↑和↓键可浏览之前输入的文本，包含所有之前执行的命令。在输入命令时按下Tab ↹键会循环显示当前可用的命令或参数，也可以快速输入准星当前指向方块的坐标（在Java版输入实体参数时，如果准星指向某个实体，则可以直接输入其UUID）。当光标在某些参数（比如一个ID）的对应位置时，在文本框上方会展示一个可用值的列表。如果这个参数已经输入了一半，这个列表则只会展示包含当前输入内容的值。\r\n<br>\r\n   \r\n在命令方块中，命令可以以前缀斜杠开头，但不是必须的。在命令方块中的命令通常还需要一个可选参数，如玩家名称。\r\n<br>\r\n    \r\n命令也可以在多人服务器的控制台中输入，但是不应加入前缀/。在英语中，以这种方式执行命令的服务器管理员被称为“ghosting”。\r\n<br>\r\n    \r\n大多数命令只能在以下状况有效：\r\n<br>\r\n    \r\n在Minecraft多人游戏服务器游戏中，由管理员或命令方块使用。\r\n<br>\r\n    \r\n在其他的多人模式游戏中，由开启局域网并允许作弊的玩家，或主持多人游戏服务器的玩家使用。\r\n<br>\r\n    \r\n在单人模式中，创建世界时开启作弊（由“更多世界选项…”按钮）。\r\n<br>\r\n    \r\n大部分命令要求执行者要有足够的权限等级，这就意味着大部分命令在单人游戏中只有在开启作弊的情况下才能被使用，并且在多人游戏服务器中需要玩家是管理员时才能使用。权限等级为0的命令所有玩家均可用。详见权限等级。\r\n<br>\r\n    \r\n注意：在Java版中，在单人游戏中，如果不允许作弊，仍可以通过公开目前的游戏至局域网（Esc→“对局域网开放”，点选“允许作弊”为开，接着“创建局域网世界”）而暂时可以作弊。你不必实际连入局域网或等待别人加入。这并不是永久的，但直至退出世界前都可以使用命令，且命令做出的修改都会被保存（物品、生物生成等）。你可以在再次开启游戏时重复以上步骤。注意对局域网开放时游戏便无法暂停，所以在公开到局域网时若有需要最好找个安全的地方。你可以重新载入世界来取消对局域网开放。\r\n<br>\r\n    \r\n在基岩版中，作弊可以随时使用设置菜单中的选项切换。开启作弊会导致玩家在那个世界永远无法获得成就，即使后来作弊被关闭。', '1.png', '2023_5_1', 'yancey', 27, 0),
(2, '坐标', '命令中的坐标可以用三个绝对坐标（X Y Z）组成的一组坐标来表示。也可以使用波浪号（~）来指定相对坐标和使用脱字符（^）来指定局部坐标。', '2.png', '2023_5_1', 'yancey', 20, 0),
(3, '目标选择器', '目标选择器是命令中使用得最频繁的参数之一，允许规范地，自由地选择将要参与命令的实体。目标选择器也在原始JSON文本中使用。\r\n<br>\r\n&nbsp; &nbsp; \r\n目标选择器分为两个部分，分别为变量和参数。\r\n<br>\r\n&nbsp; &nbsp; \r\n变量是目标选择器必要的成分，可以大致地指定要选择的实体（例如：所有实体、距离最近的玩家、与NPC交互的玩家等）。\r\n参数是可选的，负责修饰变量，可以对变量选中的实体进行进一步的过滤。参数中有许多子参数供玩家使用（例如：type、name、scores等）。', '3.png', '2023_5_1', 'yancey', 17, 0),
(4, 'NBT标签', 'NBT标签是一种树状数据结构，可以被用花括号包含的键值对（即SNBT）描述。SNBT用于Java版的命令中，用于指定玩家、实体和一些方块的较为复杂的数据。\r\n<br>\r\n&nbsp; &nbsp;\r\nSNBT由零个或多个用逗号分隔的键值对组成，且本身用大括号括住。每个键值对包含键名和键值，用冒号分开。但是，有些键值可以继续包含键值对，因此SNBT可以用来描述层次结构。\r\n<br>\r\n&nbsp; &nbsp;\r\n例： {name1:123,name2:\"sometext\",name3:{subname1:456,subname2:789}}\r\n这些NBT标签所采用的数据结构与Minecraft世界存档相同。这种数据结构在其他条目中有详细描述。NBT标签中键名有严格的限定（区分大小写），指定键名的键值也必须符合数据类型要求。些命令要求明确指明某些数值的数据类型，这可以通过在数值后附加限定字符（B、S、L、F、D）来实现。举例来说，3s表示短整型，3.2f表示单精度浮点，等等。（字符I不能限定整型。）限定字符不区分大小写。在不加限定字符，且Minecraft无法根据上下文来确定数据类型或需要比对NBT标签时，会将数据类型假定为双精度浮点型（数值有小数部分时）、整型（数值没有小数点，且在整型取值范围内）或字符串（若前述情况均不符合）。被方括号包含的集合中，被逗号分隔的元素若全为整型，则会将数据类型假定为整型数组（如[I;1,2,3]是一个整型数组，[L;1L,2L,3L]是一个长整型数组）；否则会假定为列表。但有以下特例：[]是一个空列表，而[,]是一个空整型数组。\r\n<br>\r\n&nbsp; &nbsp;\r\n如果用/clear等命令来查找NBT标签，这些命令只会检查目标实体/方块/物品是否存在此标签。这意味着一个符合条件的实体/方块/物品可能同时还拥有其他标签。这对列表和数组同样成立：只要列表或数组中包含待查找的所有元素，就会成功匹配，既不考虑元素的排列顺序，也不考虑是否有多余的元素存在。', '4.png', '2023_5_1', 'yancey', 17, 0),
(5, '原始JSON文本', '在Java版中，/tellraw和/title命令使用严格的JSON语法以显示文字。此外，部分NBT标签的值需要输入原始JSON文本。\r\n<br>\r\n&nbsp; &nbsp;\r\n在基岩版中，原始JSON文本可以作用于/tellraw的内容、/titleraw的标题、书与笔的文本（题目和作者除外）、告示牌的文本以及NPC的对话的编辑（操作员在编辑时，效果不可见）。', '5.png', '2023_5_1', 'yancey', 20, 0),
(6, 'Java版中的ID参数', '许多命令要求指定某种方块、物品、实体、药水效果、附魔、boss栏等的ID。在下文的命令语法中，这些参数会被标注为<方块>、<物品>，或在实例中用诸如minecraft:stone之类的标识符替换。这些ID都有命名空间。所有来自Minecraft的内容都使用minecraft命名空间，而来自mod及数据包的内容都使用其他命名空间。 在大多数情况下，可以省略命名空间为minecraft的ID的前缀，但是在某些情况下，例如匹配NBT标签时，需要完整的命名空间ID。\r\n<br><br>\r\n<方块><br>\r\n\r\n例子：<br>\r\nstone<br>\r\nminecraft:redstone_wire[power=15,north=up,south=side]<br>\r\nminecraft:jukebox{RecordItem:{...}}<br>\r\nminecraft:furnace[facing=north]{BurnTime:200}<br>\r\n<br>\r\n&nbsp; &nbsp;\r\n<方块>参数的格式为命名空间ID[方块状态]{NBT标签}，其中方块状态和NBT标签在不需要时可以省略。\r\n<br>\r\n&nbsp; &nbsp;\r\n方块的命名空间ID是必需的（如果没有设定命名空间则将默认为预设值：minecraft:）。\r\n<br>\r\n&nbsp; &nbsp;\r\n方块状态是可选的，位于[]内，名称与值用等号分隔，名称数值组用逗号分隔，且必须是该方块支持的状态：<br>\r\n[<方块状态名称>=<值>,<方块状态名称>=<值>,…]\r\n<br>\r\n&nbsp; &nbsp;\r\nminecraft:stone[doesntexist=purpleberry]有语法错误，因为stone（石头）没有doesntexist状态。\r\n<br>\r\n&nbsp; &nbsp;\r\nminecraft:redstone_wire[power=tuesday]有语法错误，因为redstone_wire（红石线）的power是一个在0和15之间的数字。\r\n<br>\r\n&nbsp; &nbsp;\r\nNBT标签是可选的，位于{}内。\r\n<br>\r\n&nbsp; &nbsp;\r\n如果方块参数被用于检查方块，则只会检查参数内指定了的方块状态与NBT标签是否符合。\r\n<br>\r\n&nbsp; &nbsp;\r\n如果方块参数用于放置方块，你所提供的任何状态与标签都会被设置，而其他未提供的状态与标签会被设为预设值\r\n<br>\r\n&nbsp; &nbsp;\r\n如当你设置redstone_wire[power=15]，那它将设置power到15却使north设为预设值（在这个情况下被设置为none）\r\n<br>\r\n&nbsp; &nbsp;\r\n如果方块参数被用于检查方块，则也可以填写带#前缀的方块标签的命名空间ID，如#minecraft:planks。', '6.png', '2023_5_1', 'yancey', 9, 0),
(7, '权限等级', '权限等级用于控制命令执行者可以执行什么命令。比如在Java版中，/kick不能在命令方块中执行，因为这个命令需要3的权限等级，而命令方块的权限等级仅为2。\r\n<br>\r\n&nbsp; &nbsp;\r\n在Java版中，权限等级可为0，1，2，3或4。\r\n<br>\r\n&nbsp; &nbsp;\r\n命令方块或命令方块矿车的权限等级为2。\r\n<br>\r\n&nbsp; &nbsp;\r\n服务器控制台的权限等级为4。\r\n<br>\r\n&nbsp; &nbsp;\r\n函数的权限等级为2（可在server.properties中更改）。\r\n<br>\r\n&nbsp; &nbsp;\r\n/execute执行子命令的权限等级为2。<br>\r\n<br>\r\n对于玩家来说：\r\n<br>\r\n&nbsp; &nbsp;\r\n如果该玩家是服务器的管理员，那么他的权限等级由ops.json中的值决定（默认为4）。\r\n<br>\r\n&nbsp; &nbsp;\r\n如果该玩家已经启用作弊，并在单人游戏世界中或是局域网世界的所有者，那么他的权限等级为4。\r\n<br>\r\n&nbsp; &nbsp;\r\n如果该玩家在一个允许作弊的局域网世界中，那么他的权限等级为4。否则，他的权限等级为0。\r\n<br>\r\n&nbsp; &nbsp;\r\n在基岩版中，权限等级可为0，1，2，3或4。\r\n<br>\r\n&nbsp; &nbsp;\r\n命令方块或命令方块矿车的权限等级为1。\r\n<br>\r\n&nbsp; &nbsp;\r\n服务器控制台的权限等级为4。\r\n<br>\r\n&nbsp; &nbsp;\r\n函数和附加包中的脚本的权限等级为1。\r\n<br>\r\n&nbsp; &nbsp;\r\n/execute执行子命令的权限等级为1[需要验证]。<br>\r\n<br>\r\n对于玩家来说：\r\n<br>\r\n&nbsp; &nbsp;\r\n如果该玩家在服务器中，且该玩家在“玩家权限”屏幕中的“操作员命令”开关已打开，那么他的权限等级默认为1（可在server.properties中更改）。\r\n<br>\r\n&nbsp; &nbsp;\r\n如果该玩家在单人游戏世界或局域网世界中，且该玩家在“玩家权限”屏幕中的“操作员命令”开关已打开，那么他的权限等级默认为3。\r\n<br>\r\n&nbsp; &nbsp;\r\n否则，他的权限等级为0。[需要测试]\r\n<br><br>\r\n注：命令的权限等级与暂停菜单和“玩家权限”屏幕中的身份（访客，成员，操作员或自定义）不同。例如在单人游戏世界中，身份为操作员的玩家的权限等级为3，因为其对应的“操作员命令”开关已打开；身份为访客或成员的玩家的权限等级为0，因为其对应的“操作员命令”开关没有打开。[仅基岩版]', '7.png', '2023_5_1', 'yancey', 13, 0),
(8, '命令列表及其概述', '下表列出了所有可用命令，包括即将到来的命令。', '8.png', '2023_5_1', 'yancey', 36, 0),
(9, '隐藏命令', '这类命令通常需要使用WebSocket服务器代为执行，在游戏界面中无法被执行。', '9.png', '2023_5_1', 'yancey', 19, 0),
(10, '已移除的命令', '如下图。', '10.png', '2023_5_1', 'yancey', 15, 0);

