--
-- 表的结构 `sw`
--

CREATE TABLE `sw` (
  `id` int(11) NOT NULL,
  `title` varchar(100) COLLATE utf8_unicode_ci NOT NULL,
  `new` varchar(5000) COLLATE utf8_unicode_ci NOT NULL,
  `picture` varchar(100) COLLATE utf8_unicode_ci NOT NULL,
  `time` varchar(100) COLLATE utf8_unicode_ci NOT NULL,
  `author` varchar(50) COLLATE utf8_unicode_ci NOT NULL,
  `djcs` int(11) NOT NULL,
  `shzt` int(11) NOT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- 转存表中的数据 `sw`
--

INSERT INTO `sw` (`id`, `title`, `new`, `picture`, `time`, `author`, `djcs`, `shzt`) VALUES
(1, '生成', '生物存在是因为多种原因的生成。许多生物会自然地生成，但依赖于光照等级、生物群系以及它们的环境。生物一般以相同种类成群地生成。这些生物总是会在初次区块创建时生成。许多被动生物可以繁殖，除非它们是幼年的。生物一般不会自然生成于上表面不完整的方块、水里及熔岩中，但水生生物可以在水下生成，炽足兽会生成在熔岩表面。刷怪笼是个例外，它可以让生物自然地生成于任何地方（包括空气）。\r\n<br>\r\n&nbsp; &nbsp;\r\n某些生物（像雪傀儡和凋灵）在生成之前需要玩家“构建”它们。铁傀儡既可自然生成也可由玩家构建。\r\n<br>\r\n&nbsp; &nbsp;\r\n玩家也可以使用刷怪蛋，或者在允许作弊的情况下，使用/summon命令召唤生物。', '1.png', '2023_5_1', 'yancey', 7, 0),
(2, '行为', '生物与环境的相互影响与玩家相同：受物理力学影响，且可以受到跟玩家相同来源和性质的伤害（着火、跌落和武器击伤等等）。但是某些生物拥有特殊的抗性，如下界生物（除了疣猪兽、猪灵和猪灵蛮兵）免疫火的伤害。还有一些生物可以瞬间传送（潜影贝、末影人等）。生物可以乘坐矿车、船和其他生物，并可以攀爬梯子。生物在被杀死时，会倒下发出烟雾粒子并可能掉落资源。\r\n<br>\r\n&nbsp; &nbsp;\r\nMinecraft里的每种生物都有特定的AI（人工智能）系统，拥有不同的行为和力学。如果生物附近有玩家，生物通常会随机游荡，并且通常会避免走到很高的方块而导致摔落伤害。许多生物都有一个先进的寻路系统，使它们经由复杂的路径到达要去的地点。\r\n<br>\r\n&nbsp; &nbsp;\r\n被动型生物受伤后会向随机方向逃跑。攻击型生物会在玩家靠近时面对玩家，追逐并攻击玩家。大多数生物都会察觉到在半径16格方块内的玩家，但有些可以看得更远。大多数生物可以被16个方块以外的玩家听到。\r\n<br>\r\n&nbsp; &nbsp;\r\n绝大多数生物的视线无法穿过大多数固体方块，包括半透明方块，如冰、玻璃或玻璃板。在Java版中，除了玩家和监守者以外的所有生物都不会试图在铁轨上行走，除非被其他生物推着。绝大多数生物都不会试图跳过杜鹃花丛。', '2.png', '2023_5_1', 'yancey', 7, 0),
(3, '玩家', '玩家是可以控制的生物。作为一种生物，能够影响生物的多种状态和效果也能够影响玩家。', '3.png', '2023_5_1', 'yancey', 6, 0),
(4, '盔甲架', '盔甲架是一种不会行动的生物。尽管缺少部分生物共有的特性，许多影响生物的效果会将盔甲架视为生物来处理。', '4.png', '2023_5_1', 'yancey', 6, 0),
(5, '无攻击能力的生物', '以下生物没有任何造成伤害的能力，受到伤害时通常会惊慌逃窜。\r\n<br>\r\n&nbsp; &nbsp;\r\n在Java版中，兔子有未使用的近战攻击能力。', '5.png', '2023_5_1', 'yancey', 6, 0),
(6, '无差别攻击的生物', '以下生物会无差别攻击包括玩家在内的任何生物，但不会报复性地反击目标。', '6.png', '2023_5_1', 'yancey', 6, 0),
(7, '特定攻击目标的生物', '以下生物拥有造成伤害的能力，且会攻击生物，但在任何情况下都不会试图攻击玩家。', '7.png', '2023_5_1', 'yancey', 6, 0),
(8, '条件敌对的生物', '以下生物被归类为友好生物，但具有攻击能力和敌对条件，且能够在受到侵害时反击加害者，包括玩家。\r\n<br>\r\n&nbsp; &nbsp;\r\n除狐狸外，对这一类型的生物的直接攻击造成伤害会引发反击。\r\n<br>\r\n&nbsp; &nbsp;\r\n除羊驼外，所有这一类型的生物都会持续攻击目标，直至其死亡或失踪。\r\n<br>\r\n&nbsp; &nbsp;\r\n除狐狸、铁傀儡和羊驼外，攻击这些生物会同时激怒其周围的所有同类生物。\r\n<br>\r\n&nbsp; &nbsp;\r\n由玩家建造的铁傀儡在任何情况下都不会试图攻击玩家。\r\n<br>\r\n&nbsp; &nbsp;\r\n北极熊周围有幼年北极熊时会主动攻击靠近幼年北极熊的玩家。', '8.png', '2023_5_1', 'yancey', 6, 0),
(9, '始终敌对的生物', '以下生物始终会主动攻击玩家。除潜影贝和小型史莱姆外，这些生物均会阻止附近的玩家使用床入睡。', '9.png', '2023_5_1', 'yancey', 6, 0),
(10, '条件敌对的生物', '以下生物能够被玩家激怒，但可以通过满足特定的条件，使其不会攻击玩家且不会阻止玩家入睡。', '10.png', '2023_5_1', 'yancey', 5, 0);

